$(document).ready(function() {
    $('#nav li:has(ul)').not('>a').each( function (i) {
        if (this.firstChild.tagName == null)
            $(this.firstChild).replaceWith(
                $('<a name="nodoc" href="#">' + this.firstChild.data + '</a>')
            );
    });

    var goto = location.hash || 0;
    var cb = function () {
        // Scroll to the top of the page, or to an #id.
        $.scrollTo(goto, 500); goto = 0;
        // Rewrite section links to stay in the window.
        $('#doc a[rel$=section][name]').bind( 'click', function () {
            var branch = $('#' + this.name.replace(/:/g, '\\:')).get(0);
            if (branch) {
                if (this.hash && this.hash != '#') goto = this.hash;
                $.tree_reference('tree').select_branch(branch);
            } else alert('Documentation for ' + this.name + ' not found');
            return false;
        });
    };

    var load = location.search.substring(1);
    if (!load) {
        var loc = location.pathname;
        $('meta[name="base-uri"]').each( function () {
            loc = loc.replace(this.content, '');
        });
        load = loc.replace(/[.]html$/, '').split('/').join('::');
        if (!load) load = 'toc';
    }
    var cl = $('<span class="cl">&#x25ba;</span>');
    var op = $('<span class="op">&#x25bc;</span>');
    var bu = $(
          '<span>&#x2'
        + (navigator.appVersion.indexOf("Win") == -1 ? '756' : '5a0' )
        + ';</span>'
    );
    $('#tree').tree({
        selected: load.replace(/:/g, '\\:'),
        ui: { animation: 200, theme_name: false },
        callback: {
            onopen: function(node) {
                $(node).find('.cl:first').replaceWith(op.clone());
            },
            onclose: function(node) {
                $(node).find('.op:first').replaceWith(cl.clone());
            },
            onselect: function(node, tree) {
                $(node).children('a:first').addClass('sel');
                $(node).children('a:first[name!=nodoc]').each( function () {
                    var url = this.href;
                    $('#doc').load(url, function (text, status, xhr) {
                        if (xhr.status == 200) { return cb() }
                        $('#doc').html(
                            '<h4 class="oops">' + xhr.statusText + ': '
                            + url + '</h4'
                        );
                    });
                });
                tree.toggle_branch(node);
            },
            ondeselect: function (node) {
                $(node).children('a:first').removeClass('sel');
            }
        }
    });
    $('#nav li.leaf > a').prepend(bu);
    $('#nav li:has(ul) > a').prepend(cl);
});
